mapper.update:
update t_card
set state = #{state}
where account = #{account}

update t_card
set balance = balance + #{amount}
where account = #{account}

update t_card
set balance = balance - #{amount},
    version = version + 1
where id = #{id}
  and version = #{version}
  and balance >= #{amount}

mapper.select:
select SQL_CALC_FOUND_ROWS
    id,
    balance,
    state,
    account
from t_card



            id=#{param.id}


            and state = #{param.state}


            and account = #{param.account}



order by id asc

    LIMIT #{skipCount},#{limit}
