mapper.update:
update t_card
set state = #{state}
where account = #{account}

update t_card
set balance = balance + #{amount}
where account = #{account}
  and state = #{state}

mapper.select:
select user, balance,`lock`
from t_card
where user = #{user}

select SQL_CALC_FOUND_ROWS
    id,
    balance,
    state,
    account
from t_card



            id=#{param.id}


            and state = #{param.state}


            and account = #{param.account}



order by id asc

    LIMIT #{skipCount},#{limit}
