mapper.select:
select DATE_FORMAT(createTime, '%Y-%m-%d') AS date, sum(price) AS price, count(id) AS count, type AS consumeType from t_consume
type not in ('PAYMENT','RECHARGE','REFUND')
and createTime >= #{startTime}
and createTime <= #{endTime}
group by DAY(createTime),type order by date;

SELECT sum(price)       as sum,
       count(id)        as count,
       ceil(avg(price)) as avg,
       max(price)       as max
FROM t_consume
WHERE user = #{id}
  AND createTime >= #{start}
  AND createTime <= #{end}

SELECT type,
       sum(price) as price
FROM t_consume
WHERE user = #{id}
  AND createTime >= #{start}
  AND createTime <= #{end}
  and type not in ('PAYMENT', 'RECHARGE', 'REFUND')
GROUP BY type
