mapper.select:
select SQL_CALC_FOUND_ROWS id, account, level1, level2, level3, state, out_id, in_id, amount, create_time, description, creator, version from t_trade
id = #{param.id}
and account = #{param.account}
and create_time >= #{param.startCreateTime}
and create_time <= #{param.endCreateTime}
order by id asc LIMIT #{skipCount},#{limit}
